mapper.select:
select sulkit_name_kor, sulkit_name_eng, sulkit_desc, sulkit_recipe, sulkit_price, sulkit_img
from sulkit
where sulkit_number = ${sulkitNumber}

SELECT product_name_kor, product_name_eng, product_price, product_ea, product_vol, product_alcohol_degree, category_number, product_system_name
FROM product;
where product_number = ${productNumber}
